implementation.constructor:

        with (virtualIdentityExtension.LIB) { 
		if (vI.notificationBar) vI.notificationBar.dump("## identitySelector: constructor\n");
        var AccountManager = Components.classes["@mozilla.org/messenger/account-manager;1"]
            .getService(Components.interfaces.nsIMsgAccountManager);
        var debugNotes = vI.prefDialog.preferences.getBoolPref("debug_notification");

        for (let i = AccountManager.accounts.Count() - 1; i >= 0 ; i--) {
            var account = AccountManager.accounts.QueryElementAt(i, Components.interfaces.nsIMsgAccount);
            for (let j = account.identities.Count() - 1; j >= 0 ; j--) {
                var identity = account.identities.QueryElementAt(j, Components.interfaces.nsIMsgIdentity);
                const XUL_NS = "http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul";
                var listitem = document.createElementNS(XUL_NS, "listitem"); // create a new XUL listitem
                listitem.setAttribute("class", "selectorListitem");
                listitem.setAttribute("label", identity.identityName);
                listitem.setAttribute("value", identity.key);
                listitem.setAttribute("description", " - " + account.incomingServer.prettyName);
                listitem.setAttribute("debugNotesHidden", !debugNotes);
                if (this.firstChild) this.insertBefore(listitem, this.firstChild);      // ensure that new elements are visible
                else this.appendChild(listitem);                                        // otherwise checkbox will not be initialized
            }
        }
        if (debugNotes)
            document.getAnonymousElementByAttribute(this, "id", "ignoreIDs_debug").removeAttribute("hidden")
		}

property.setter:
        with (virtualIdentityExtension.LIB) { 
		if (vI.notificationBar) vI.notificationBar.dump("## identitySelector: setting value to '" + val + "'\n");
        var listItems = this.childNodes;
        for (var index = 0; index < listItems.length; index++) {
            var key = listItems[index].getAttribute("value");
            listItems[index].checked = (val.indexOf(":" + key + ":") == -1);
            if (vI.notificationBar) vI.notificationBar.dump("## identitySelector: setting value, '" + listItems[index].getAttribute("value") + "' '" + listItems[index].checked + "' '" + listItems[index].getAttribute("checked") + "'\n")
        }
        this._value = val;
		}

property.getter:
        with (virtualIdentityExtension.LIB) { 
		if (vI.notificationBar) vI.notificationBar.dump("## identitySelector: getting value\n");
        var returnVal = "";
        var listItems = this.childNodes;
        for (var index = 0; index < listItems.length; index++) {
            if (vI.notificationBar) vI.notificationBar.dump("## identitySelector: getting value, '" + listItems[index].getAttribute("value") + "' '" + listItems[index].checked + "' '" + listItems[index].getAttribute("checked") + "'\n");
            if (!listItems[index].checked) returnVal += " :" + listItems[index].getAttribute("value") + ": "
        }
        if (vI.notificationBar) vI.notificationBar.dump("## identitySelector: getting value, result '" + returnVal + "'\n");
        return returnVal;
		}
